import React, { useState, useEffect } from "react";
import { Link, useParams } from "react-router-dom";
import style from "./Detail.module.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
const URL_BASE = "https://rickandmortyapi.com/api"
const API_KEY = "ae548ca5efd4.e18bd88c702829bbd7f8"

function Detail() {
    const { detailId } = useParams();
    const [character, setCharacter] = useState({});
  
    useEffect(() => {
      fetch(`${URL_BASE}/character/${detailId}?key=${API_KEY}`)
        .then((response) => response.json())
        .then((char) => {
          if (char.name) {
            setCharacter(char);
          } else {
            window.alert("No hay personajes con ese ID");
          }
        })
        .catch((err) => {
          window.alert("No hay personajes con ese ID");
        });
      return () => setCharacter({});
    }, [detailId]);
  
    return (
      <div className={style.container}>
        <Link to="/" className={style.link} ><FontAwesomeIcon icon="fa-regular fa-angle-left" /></Link>
        <div className={style.details}>
          <img className={style.image} src={character.image} alt={character.name} />
        </div>
        <div className={style.info}>
            <h2 className={style.h2}>{character.name}</h2>
            <p>Status: <span>{character.status}</span></p>
            <p>Species: <span>{character.species}</span></p>
            <p>Type: <span>{character.type || "Unknown"}</span></p>
            <p>Gender: <span>{character.gender}</span></p>
            <p>Origin: <span>{character.origin && character.origin.name}</span></p>
            <p>Location: <span>{character.location && character.location.name}</span></p>
        </div>
      </div>
    );
  }

export default Detail;
