const AGREGAR = "AGREGAR";
const ELIMINAR = "ELIMINAR";

export function addFavCharacter(character){
    return {
        type: AGREGAR,
        payload: character
    }
}

export function deleteFavCharacter(id){
    return{
        type: ELIMINAR,
        payload: id
    }
}
