import { AGREGAR, ELIMINAR, FILTER, ALL_FILTER, ORDER } from "./types";

export function addFavCharacter(character){
    return {
        type: AGREGAR,
        payload: character
    }
}

export function deleteFavCharacter(id){
    if(id === "All"){
        return{
            type: ALL_FILTER
        }
    }
    return{
        type: ELIMINAR,
        payload: id
    }
}

export function filterCards(gender){
    return{
        type: FILTER,
        payload: gender
    }
}

export function orderCards(id){
    return {
        type: ORDER,
        payload: id
    }
}
