import React, { useState } from "react";
import { validateUsername, validatePassword } from "./validation";
import styles from "./Form.module.css";

function Form(){
    const [userData, setUserData] = useState({username: "", password: "",});

    function handleInputChange(event) {
        const { name, value } = event.target;
        setUserData((prevUserData) => ({...prevUserData, [name]: value}));
    }

    return(
        <form className={styles.form}>
            <h1 className={styles.title}>Iniciar Sesión</h1>
            <div className={styles.item}>
                <label className={styles.label}>Username:</label>
                <input type="text" name="username" placeholder="Escribe tu nombre de usuario..." className={styles.input} value={userData.username} onChange={handleInputChange} />
            </div>
            <div className={styles.item}>
                <label className={styles.label}>Password:</label>
                <input type="text" name="password" placeholder="Escribe tu contraseña..." className={styles.input} value={userData.password} onChange={handleInputChange} />
            </div>
            <button className={styles.button}>Ingresar</button>
        </form>
    )
}

export default Form;
