import React, { useState, useEffect } from "react";
import { Link, useParams } from "react-router-dom";
import style from "./Detail.module.css";
const URL_BASE = "https://rickandmortyapi.com/api"
const API_KEY = "ae548ca5efd4.e18bd88c702829bbd7f8"

function Detail() {
    const { detailId } = useParams();
    const [character, setCharacter] = useState({});
  
    useEffect(() => {
        fetch(`${URL_BASE}/character/${detailId}?key=${API_KEY}`)
          .then((response) => response.json())
          .then((data) => {
            if (data.name) {
              setCharacter(data);
            } else {
              window.alert("No hay personajes con ese ID");
            }
          })
          .catch((err) => {
            console.error(err);
            window.alert("Hubo un error al cargar los datos");
          });
      }, [detailId]);
  
    return (
      <div className={style.container}>
        <Link to="/">Volver</Link>
        <div className={style.details}>
        <img src={character.image} alt={character.name} />
        <div className={style.info}>
        console.log(character)
            <h2>{character.name}</h2>
            <p>Status: {character.status}</p>
            <p>Species: {character.species}</p>
            <p>Type: {character.type || "Unknown"}</p>
            <p>Gender: {character.gender}</p>
            <p>Origin: {character.origin && character.origin.name}</p>
            <p>Location: {character.location && character.location.name}</p>
        </div>
        </div>
      </div>
    );
  }

export default Detail;
