import React, { useState } from 'react';
import {FaRandom} from "react-icons/fa";
import styles from "./SearchBar.module.css";

function SearchBar(props) {
  const [character, setCharacter] = useState("");

  const handleInputChange = (event) => {
    setCharacter(event.target.value);
  }

  const handleSearch = () => {
    props.onSearch(character);
    setCharacter("");
  }

  const handleRandom = () => {
    props.handleRandom();
  };

  return (
    <div className={styles.divMain}>
      <input className={styles.inputClass} type='search' value={character} onChange={handleInputChange} />
      <button className={styles.buttonClass} onClick={handleSearch}>Agregar</button>
      <button className={styles.buttonClass} onClick={handleRandom} ><FaRandom icon="fa-solid fa-shuffle" /></button>
    </div>
  );
}

export default SearchBar;
