// import React, { useState, useEffect } from "react";
// import { Link, useParams } from "react-router-dom";
// import style from "./Detail.module.css";
const URL_BASE = "https://rickandmortyapi.com/api"
const API_KEY = "ae548ca5efd4.e18bd88c702829bbd7f8"

import React from 'react';
import { useParams } from 'react-router-dom';

function Detail({ characters }) {
  const { detailId } = useParams();

  // Buscamos el personaje en el array de characters que tiene el mismo id que el detailId
  const character = characters.find((char) => char.id === parseInt(detailId));

  // Si el personaje no existe, mostramos un mensaje de error
  if (!character) {
    return <h2>Personaje no encontrado</h2>;
  }

  return (
    <div>
      <h2>{character.name}</h2>
      <img src={character.image} alt={character.name} />
      <p>Status: {character.status}</p>
      <p>Species: {character.species}</p>
      <p>Type: {character.type}</p>
      <p>Gender: {character.gender}</p>
    </div>
  );
}

export default Detail;
